def bubble_sort(array)
    while true do
        i = 0
        x = 0
        while i < array.length
            y = i + 1
            while y < array.length
                if array[i] > array[y]
                    holder = array[i]
                    array[i] = array[y]
                    array[y] = holder
                    x += 1
                end
                y += 1
            end
            i += 1
        end
        break if x == 0
    end
array
end
    

